class ArticleContent {
  ArticleContent({
      int? id, 
      String? slug, 
      String? date, 
      String? titleEng, 
      String? descriptionEng, 
      String? titleHindi, 
      String? descriptionHindi, 
      List<Tag>? tag,}){
    _id = id;
    _slug = slug;
    _date = date;
    _titleEng = titleEng;
    _descriptionEng = descriptionEng;
    _titleHindi = titleHindi;
    _descriptionHindi = descriptionHindi;
    _tag = tag;
}

  ArticleContent.fromJson(dynamic json) {
    _id = json['id'];
    _slug = json['slug'];
    _date = json['date'];
    _titleEng = json['title_eng'];
    _descriptionEng = json['description_eng'];
    _titleHindi = json['title_hindi'];
    _descriptionHindi = json['description_hindi'];
    if (json['tag'] != null) {
      _tag = [];
      json['tag'].forEach((v) {
        _tag?.add(Tag.fromJson(v));
      });
    }
  }
  int? _id;
  String? _slug;
  String? _date;
  String? _titleEng;
  String? _descriptionEng;
  String? _titleHindi;
  String? _descriptionHindi;
  List<Tag>? _tag;

  int? get id => _id;
  String? get slug => _slug;
  String? get date => _date;
  String? get titleEng => _titleEng;
  String? get descriptionEng => _descriptionEng;
  String? get titleHindi => _titleHindi;
  String? get descriptionHindi => _descriptionHindi;
  List<Tag>? get tag => _tag;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['slug'] = _slug;
    map['date'] = _date;
    map['title_eng'] = _titleEng;
    map['description_eng'] = _descriptionEng;
    map['title_hindi'] = _titleHindi;
    map['description_hindi'] = _descriptionHindi;
    if (_tag != null) {
      map['tag'] = _tag?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

