class Tag {
  Tag({
      String? name, 
      String? slug,}){
    _name = name;
    _slug = slug;
}

  Tag.fromJson(dynamic json) {
    _name = json['name'];
    _slug = json['slug'];
  }
  String? _name;
  String? _slug;

  String? get name => _name;
  String? get slug => _slug;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['name'] = _name;
    map['slug'] = _slug;
    return map;
  }

}